secureScript.script:
if ( Data_Creation_Scenario.equals('20_Students') || Data_Creation_Scenario.equals('1052') || Data_Creation_Scenario.equals('1054') ){
inputBox="<input type='text' name='value'>"
 return inputBox
}
else
{
inputBox="<input type='text' placeholder='NOT_REQUIRED' disabled>"
 return inputBox
}

hudson.tasks.BatchFile.command:
@echo off
cd jmeter/bin
if exist outfilenames.txt del outfilenames.txt
SET time=%BUILD_TIMESTAMP%
set var=%BUILD_TIMESTAMP: =%
set var1=%var::=%
SET finalfname=%Data_Creation_Scenario%_%var1%
SET finalfname1=%Data_Creation_Scenario%_%var1%_1.csv
SET finalfname2=%Data_Creation_Scenario%_%var1%_2.csv
SET finalfname3=%Data_Creation_Scenario%_%var1%_3.csv
SET finalfname4=%Data_Creation_Scenario%_%var1%_4.csv
if "%Data_Creation_Scenario%"=="1055" (
echo FINALFNAME = %finalfname1%,%finalfname2%,%finalfname3%,%finalfname4% >> outfilenames.txt
) else (
if "%Data_Creation_Scenario%"=="1054" (
node uploadFile.js NOT_REQUIRED
) else ( 
        if "%Data_Creation_Scenario%"=="1041" (
          echo FINALFNAME = %finalfname1%,%finalfname2%,%finalfname3% >> outfilenames.txt
        ) else (
               if "%Data_Creation_Scenario%"=="1044" (
                echo FINALFNAME = %finalfname1%,%finalfname2%,%finalfname3% >> outfilenames.txt
               ) else (
                      echo FINALFNAME = %finalfname1% >> outfilenames.txt
                 )
          )
   )
)
jmeter -n -t data_creation_nongui_All.jmx -Jusers=%users% -Jrampup=%rampup% -JData_Creation_Scenario=%Data_Creation_Scenario% -JInputFileName=%Input_File_Name% -JOutputFileName=%finalfname% -l outputfromjenkins.jtl

@echo off
cd jmeter/uploadFile
SET time=%BUILD_TIMESTAMP%
set var=%BUILD_TIMESTAMP: =%
set var1=%var::=%
SET finalfname=%Data_Creation_Scenario%_%var1%.csv
SET finalfname1=%Data_Creation_Scenario%_%var1%_1.csv
SET finalfname2=%Data_Creation_Scenario%_%var1%_2.csv
SET finalfname3=%Data_Creation_Scenario%_%var1%_3.csv
SET finalfname4=%Data_Creation_Scenario%_%var1%_4.csv
if "%Data_Creation_Scenario%"=="1055" (
node uploadFile.js %finalfname1% %finalfname2% %finalfname3% %finalfname4%
) else (
if "%Data_Creation_Scenario%"=="1054" (
node uploadFile.js NOT_REQUIRED
) else ( 
        if "%Data_Creation_Scenario%"=="1041" (
          node uploadFile.js %finalfname1% %finalfname2% %finalfname3%
        ) else (
               if "%Data_Creation_Scenario%"=="1044" (
                node uploadFile.js %finalfname1% %finalfname2% %finalfname3%
               ) else (
                      npm test %finalfname%
                 )
          )
   )
)
